import React from 'react';
import './Body.css';
import Items from '../Items/Items';

const Body = (props) => {
  return (
    <div id="app-body">
        <div id="all-items" className="items-container" style={{marginLeft: -(props.tab - 1)*100 + '%'}}>
            <div id="all-items-container">
                <span className="no-items-message">No Items</span>
                <Items items={props.items} onItemClick={props.onItemClick} onDeleteItem={props.onDeleteItem}></Items>
            </div>
        </div>
    </div>
  );
}

export default Body;
