import React from 'react';
import './Footer.css';

class Footer extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            content: "",
            name: "",
            number: ""
        }

        this.handleChange = this.handleChange.bind(this);
    }

    // handleChange(event){
    //   const value = event.target.value;
    //     this.setState({ content: value,
    //                     name: value,
    //                     number: value
    //                   });
    // }
    handleChange(evt) {
      const value = evt.target.value;
      setState({
        ...state,
        [evt.target.name]: value
      });
    }


    render() {
        return (<footer>
            <input value={state.content} onChange={handleChange} id="content" type="text" placeholder="what you have to do..." />
            <input value={state.name} onChange={handleChange} id="name" type="text" placeholder="Name" />
            <input value={state.number} onChange={handleChange} id="number" type="text" placeholder="Number" />
            <span id="add-btn" onClick={() => this.props.createItem(this.state.content)}>Add</span>
        </footer>)

    }
}

export default Footer;
