import React from 'react';
import './Footer.css';

class Footer extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            content: "",
            name: "",
            number: ""
        }

        this.changeContent = this.changeContent.bind(this);
        this.changeName = this.changeName.bind(this);
    }

    changeContent(event){
        this.setState({ content: event.target.value
                      });
    }

    changeName(event){
        this.setState({ name: event.target.value
                      });
    }


    render() {
        return (<footer>
            <input value={this.state.content} onChange={this.changeContent} id="content" type="text" placeholder="what you have to do..." />
            <input value={this.state.name} onChange={this.changeName} id="name" type="text" placeholder="Name" />
            <input value={this.state.number} onChange={this.changeName} id="number" type="text" placeholder="Number" />
            <span id="add-btn" onClick={() => this.props.createItem(this.state.content)}>Add</span>
        </footer>)

    }
}

export default Footer;
