import React from 'react';
import './App.css';
import Header from './components/Header/Header.js';
import Body from './components/Body/Body.js';
import Footer from './components/Footer/Footer.js';

import AddNewItem from './model/AddNewItem';
import AddNew from './model/AddNew';

class App extends React.Component {

  constructor(props) {
  super(props);

  this.phonebook = new AddNew();

  this.state = {
    activeTab: -1,
    items: this.phonebook.items
  };

  this.changeTab = this.changeTab.bind(this);
  this.examinePath = this.examinePath.bind(this);
  this.createItem = this.createItem.bind(this);
  this.deleteItem = this.deleteItem.bind(this);

  }

  componentDidMount() {
    this.examinePath();
    window.onpopstate = this.examinePath;
  }

  examinePath() {

  switch (window.location.pathname) {
    case "/all-items":
    case "":
    default:
      this.setState({ activeTab: 1 });
      break;
    case "/pending-items":
    case 2:
      this.setState({ activeTab: 2 });
      break;
    case "/completed-items":
    case 3:
      this.setState({ activeTab: 3 });
      break;
  }

}


changeTab(tab) {

  if (this.state.activeTab === tab)
    return

  switch (tab) {
    case 1:
    default:
      this.setState({ activeTab: 1 });
      window.history.pushState(null, "Tab1", "/all-items");
      break;
    case 2:
      this.setState({ activeTab: 2 });
      window.history.pushState(null, "Tab2", "/pending-items");
      break;
    case 3:
      this.setState({ activeTab: 3 });
      window.history.pushState(null, "Tab3", "/completed-items");
      break;
  }

}

  createItem(firstname, lastname, number) {
    let item = new AddNewItem(firstname, lastname, number, new Date(),);
    let items = this.phonebook.add(item);
    this.setState({items: items});

  }

  deleteItem(item) {
    let items = this.phonebook.delete(item);
    this.setState({ items: items });
  }

  render () {
      return (<div id="app-container">
      <Header></Header>
      <Body items={this.state.items} onDeleteItem={this.deleteItem}></Body>
      <Footer createItem={this.createItem}></Footer>
      </div>);
  }
}

export default App;
